--
create table if not exists mouhafaza
(
    key                      			varchar(10) not null,
    name_en_us							text not null,
    name_ar_lb							text not null,
    description_en_us                   text not null,
    description_ar_lb                   text not null,
    active								boolean not null,
    create_user_id                      int not null,
    create_timestamp                    timestamp with time zone not null,
    constraint pk_mouhafaza primary key(key)
);

create table if not exists kadaa
(
    key                      			varchar(10) not null,
    mouhafaza_key                       varchar(10) not null,
    name_en_us							text not null,
    name_ar_lb							text not null,
    description_en_us                   text not null,
    description_ar_lb                   text not null,
    active								boolean not null,
    create_user_id                      int not null,
    create_timestamp                    timestamp with time zone not null,
    constraint pk_kadaa primary key(key),
    constraint fk_kadaa_mouhafaza foreign key (mouhafaza_key) references mouhafaza (key) on delete cascade
);

create table if not exists project
(
	id                      			int not null generated by default as identity,
	kadaa_key                           varchar(10) not null,
	name_en_us							text not null,
	name_ar_lb							text not null,
	description_en_us					text null,
	description_ar_lb					text null,
    update_user_id                      int not null,
    update_timestamp                    timestamp with time zone not null,
    create_user_id                      int not null,
    create_timestamp                   	timestamp with time zone not null,
    constraint pk_project primary key(id),
    constraint fk_project_kadaa foreign key (kadaa_key) references kadaa (key) on delete cascade
);

create table if not exists coordinate
(
	id                      			int not null generated by default as identity,
	project_id                          int not null,
	latitude							double precision,
	longitude							double precision,
    constraint pk_coordinate primary key(id),
    constraint fk_coordinate_project foreign key (project_id) references project (id) on delete cascade
);

